        jButton3.addActionListener(new java.awt.event.ActionListener() {
            public void actionPerformed(java.awt.event.ActionEvent evt) {
            	
            	 String countryvisiting= cntrycb.getActionCommand();
                 String currentuniversity= jComboBox1.getActionCommand();
                 String abroaduniversity= jComboBox2.getActionCommand();
                 String selectcources = jComboBox3.getActionCommand();
                 String collegename = jTextField1.getText();
                 String studentname =jTextField2.getText();
                
                 try {
         			Connection connect = DriverManager.getConnection(url, username, pwd);
         			String query = "INSERT INTO studentvisa values('" + countryvisiting + "','" + currentuniversity + "','"+ abroaduniversity +"','"+ selectcources +"','"+ collegename +"','" +studentname+
         					"')";
         			Statement sta = connect.createStatement();
         			int x = sta.executeUpdate(query);
         			
         			if (x == 0) {
         				JOptionPane.showMessageDialog(jButton3, "please fill all the details");
         			} else {
         				JOptionPane.showMessageDialog(jButton3,
         						"your form is successfully submitted");
         			}
         			connect.close();
         		} catch (SQLException e1) {
         			System.out.println("connection issues");
         			e1.printStackTrace();
         		}
             }
         });
        

