    private void up1btnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_up1btnActionPerformed
     JFileChooser chooser= new JFileChooser();
       chooser.showOpenDialog(null);
       File f=chooser.getSelectedFile();
        String filename = f.getAbsolutePath();
       f1label.setText(filename);
    }//GEN-LAST:event_up1btnActionPerformed

